cmt AS edition_cmt, p.input_code AS input_code, p.lbl_bc AS lbl_bc, p.seq_num AS seq_num, p.description AS description, p.prod_prop_1 AS prod_prop_1, p.prod_prop_2 AS prod_prop_2, p.prod_status AS prod_status, p.opr_id AS opr_id, p.upd_time AS upd_time, p.syn_sn AS syn_sn, p.is_del AS is_del, p.create_by AS create_by, p.create_time AS create_time, p.update_by AS update_by, p.update_time AS update_time, p.sellingPoints AS sellingPoints, p.ingredient AS ingredient FROM `UNIT_PROD_CLS` c INNER JOIN `prod_cls` p ON p.`prod_cls_id` = c.`prod_cls_id` LEFT JOIN sys_prsnl ps ON p.`opr_id` = ps.`prsnl_id` INNER JOIN sys_unit u ON c.`unit_id` = u.`unit_id` LEFT JOIN vender v ON c.`vender_id` = v.`vender_id` LEFT JOIN sys_unit ui ON v.`vender_id` = ui.`unit_id` INNER JOIN brand bra ON bra.`brand_id` = p.`brand_id` LEFT JOIN color co ON p.`color_id` = co.`color_id` LEFT JOIN `spec_grp` sp ON p.`spec_grp_id` = sp.`spec_grp_id` LEFT JOIN `spec_scp` scp ON p.`spec_scp_id` = scp.`spec_scp_id` LEFT JOIN sys_unit suv ON p.`mfr_id` = suv.unit_id LEFT JOIN sys_unit dsun ON p.`dsgn_unit_id` = dsun.`unit_id` LEFT JOIN sys_prsnl dsunpr ON p.`stylist_id` = dsunpr.`prsnl_id` LEFT JOIN spec sss ON p.`spec_id` = sss.`spec_id` LEFT JOIN sys_code_dtl d1 ON p.`sub_model` = d1.`code` AND d1.`code_type` = "SUB_STYLE" LEFT JOIN sys_code_dtl d2 ON p.`gender` = d2.`code` AND d2.`code_type` = "GENDER" LEFT JOIN sys_code_dtl d3 ON p.`suite_prop` = d3.`code` AND d3.`code_type` = "SUITE_PROP" LEFT JOIN sys_code_dtl d4 ON p.`main_style` = d4.`code` AND d4.`code_type` = "MAIN_STYLE" LEFT JOIN sys_code_dtl d5 ON p.`assis_style` = d5.`code` AND d5.`code_type` = "MAIN_STYLE" LEFT JOIN sys_code_dtl d6 ON p.`uom` = d6.`code` AND d6.`code_type` = "UOM" LEFT JOIN sys_code_dtl d7 ON p.`season` = d7.`code` AND d7.`code_type` = "season" LEFT JOIN sys_code_dtl d8 ON p.`mkt_grd` = d8.`code` AND d8.`code_type` = "MKT_GRD" LEFT JOIN sys_code_dtl d9 ON p.`mkt_sort` = d9.`code` AND d9.`code_type` = "MKT_SORT" LEFT JOIN sys_code_dtl d10 ON p.`mkt_type` = d10.`code` AND d10.`code_type` = "MKT_TYPE" LEFT JOIN sys_code_dtl d11 ON p.`sales_chnl` = d11.`code` AND d11.`code_type` = "SALES_CHNL" LEFT JOIN sys_code_dtl d12 ON p.`prc_lvl` = d12.`code` AND d12.`code_type` = "PRC_LVL" LEFT JOIN sys_code_dtl d13 ON p.`edition` = d13.`code` AND d13.`code_type` = "EDITION" LEFT JOIN sys_code_dtl d14 ON p.`out_mtrl` = d14.`code` AND d14.`code_type` = "ASSIS_STYLE" LEFT JOIN sys_code_dtl d15 ON p.`in_mtrl` = d15.`code` AND d15.`code_type` = "CUP_IN_MTRL" LEFT JOIN sys_code_dtl d16 ON p.`prod_std` = d16.`code` AND d16.`code_type` = "PROD_STD" LEFT JOIN sys_code_dtl d17 ON p.`prod_grd` = d17.`code` AND d17.`code_type` = "CCR_LVL" LEFT JOIN sys_code_dtl d18 ON p.`prod_style` = d18.`code` AND d18.`code_type` = "PROD_STYLE" LEFT JOIN sys_code_dtl d19 ON p.`story_line` = d19.`code` AND d19.`code_type` = "STORY_LINE" LEFT JOIN sys_code_dtl d20 ON p.`stk_grd` = d20.`code` AND d20.`code_type` = "STK_GRD" LEFT JOIN sys_code_dtl d21 ON p.`inner_pck` = d21.`code` AND d21.`code_type` = "INNER_PCK" LEFT JOIN sys_code_dtl d22 ON p.`pck_type` = d22.`code` AND d22.`code_type` = "PCK_type" LEFT JOIN sys_code_dtl d23 ON c.`sales_mode` = d23.`code` AND d23.`code_type` = "RT_MODE" LEFT JOIN sys_code_dtl d24 ON p.`mfr_brand_id` = d24.`code` AND d24.`code_type` = "BRAND_ID" LEFT JOIN sys_code_dtl d25 ON p.`prod_line` = d25.`code` AND d25.`code_type` = "PROD_LINE" LEFT JOIN spec_grp d26 ON p.`spec_grp_id` = d26.`spec_grp_id` LEFT JOIN sys_code_dtl d27 ON p.`auditType` = d27.code AND d27.`code_type` = 'auditType' WHERE p.`is_del` = 1 and p.`prod_status` != 'D' AND c.unit_id =#{unitId} /*商品品种Id*/
and p.prod_cls_id = #{prodClsId,jdbcType=BIGINT}
/*商品代码*/
