SELECT b.prod_cls_id AS prodClsId, b.`prod_name` AS title, d.brand_name AS brandName, d.brandCode as brandCode, b.`prod_cls_code` AS `code`, b.`input_code` AS mnemonicCode, DATE_FORMAT(b.`create_time`, "%Y-%m-%d %T") AS createTime, DATE_FORMAT(b.sales_date, "%Y-%m-%d %T") AS timeToMarket, b.`year_val` AS `year`, b.`unit_wgt` AS weight, b.`unit_vol` AS volume, b.`uom` AS measureUnit, b.season AS season, b.`sellingPoints` AS sellingPoint, b.`prod_style` AS productStyle, c.`description` AS thickness, b.main_style AS mainStyle, b.sub_style AS subStyle, b.`out_mtrl` AS material, db.`sup_code` as supplierCode, u.`rt_unit_price` as salePrice FROM prod_cls b INNER JOIN brand d ON b.`brand_id` = d.`brand_id` LEFT JOIN sys_code_dtl c ON c.`code_type` = 'MFR_BRAND_ID' AND b.`mfr_brand_id` = c.`code` inner join unit_prod_cls u on u.`unit_id` = b.`ctrl_unit_id` and u.`prod_cls_id` = b.`prod_cls_id` /*存储仓库*/ INNER JOIN sys_unit us ON b.`prod_line` = us.`unit_code` inner join dh_b_sup_wareh db on us.`unit_id` = db.`wareh_id` where b.`prod_status` = 'A'
and b.`prod_cls_id` = #{prodClsId}
and b.`prod_cls_code` = #{prodClsCode}

