)
AND p.`prod_cls_id` =0

SELECT p.`isAudit` as isAudit, p.`auditType` AS auditType, d1.`description` AS subModelCp, d2.`description` AS genderCp, d3.`description` AS suitePropCp, d4.`description` AS mainStyleCp, d5.`description` AS assisStyleCp, d6.`description` AS uomCp, d7.`description` AS seasonCp, d8.`description` AS mktGrdCp, d9.`description` AS mktSortCp, d10.`description` AS mktTypeCp, d11.`description` AS salesChnlCp, d12.`description` AS prcLvlCp, d13.`description` AS editionCp, d14.`description` AS outMtrlCp, d15.`description` AS inMtrlCp, d16.`description` AS prodStdCp, d17.`description` AS prodGrdCp, d18.`description` AS prodStyleCp, d19.`description` AS storyLineCp, d20.`description` AS stkGrdCp, d21.`description` AS innerPckCp, d22.`description` AS pckTypeCp, d23.`description` AS salesModeCp, d24.`description` AS mfrBrandIdCp, d25.`description` AS prodLineCp, d26.`spec_grp_name` AS SpecIdCp, d27.description AS auditTypeCp, sss.`spec_code` AS specCode, suv.`unit_code` AS mftCode, suv.unit_name AS mftName, dsunpr.`prsnl_code` AS dsunPrsnlCode, dsunpr.`full_name` AS dsunFullName, dsun.`unit_code` AS dsgnUnitCode, dsun.`unit_name` AS dsgnUnitName, u.`unit_code` AS unit_code, u.`unit_name` AS unit_name, ui.`unit_name` AS vender_name, ui.`unit_code` AS vender_code, ps.`prsnl_code` AS `prsnl_code`, ps.`full_name` AS `full_name`, bra.`brand_name` AS brand_name, sp.`spec_grp_name` AS spec_grp_name, scp.`spec_scp_name` AS spec_scp_name, co.`color_code` AS color_code, co.`color_name` AS color_name, c.unit_id AS unit_id, c.sales_mode AS sales_mode, c.rcmd_lvl AS rcmd_lvl, c.rt_unit_price AS rt_unit_price, c.ws_unit_price AS ws_unit_price, c.ws_tax_rate AS ws_tax_rate, c.pu_unit_price AS pu_unit_price, c.pu_tax_rate AS pu_tax_rate, c.vender_id AS vender_id, p.is_weed AS is_weed, p.prod_cls_id AS prod_cls_id, p.prod_cls_code AS prod_cls_code, p.prod_name AS prod_name, p.prod_cat_id AS prod_cat_id, p.brand_id AS brand_id, p.gender AS gender, p.suite_prop AS suite_prop, p.suite_cls_id AS suite_cls_id, p.model AS model, p.sub_model AS sub_model, p.main_style AS main_style, p.assis_style AS assis_style, p.sub_style AS sub_style, p.uom AS uom, p.qty_digit AS qty_digit, p.year_val AS year_val, p.season AS season, DATE_FORMAT(p.sales_date, "%Y-%m-%d %T") AS sales_dateCp, DATE_FORMAT(p.expd_date, "%Y-%m-%d %T") AS expd_dateCp, p.batch_num AS batch_num, p.mkt_grd AS mkt_grd, p.mkt_sort AS mkt_sort, p.mkt_type AS mkt_type, p.sales_chnl AS sales_chnl, p.prc_lvl AS prc_lvl, p.lst_price AS lst_price, p.out_mtrl AS out_mtrl, p.in_mtrl AS in_mtrl, p.stuffing AS stuffing, p.technology AS technology, p.dsgn_desc AS dsgn_desc, p.self_made AS self_made, p.mtrl_perd AS mtrl_perd, p.pdn_perd AS pdn_perd, p.ado_perd AS ado_perd, p.min_pdn_qty AS min_pdn_qty, p.min_ado_qty AS min_ado_qty, p.pdn_cost AS pdn_cost, p.end_of_pdn AS end_of_pdn, p.origin AS origin, p.mfr_id AS mfr_id, p.mfr_brand_id AS mfr_brand_id, p.prod_std AS prod_std, p.prod_grd AS prod_grd, p.dsgn_unit_id AS dsgn_unit_id, p.stylist_id AS stylist_id, p.pbp_num AS pbp_num, p.prod_style AS prod_style, p.prod_line AS prod_line, p.story_line AS story_line, p.is_sample AS is_sample, p.sample_num AS sample_num, p.stk_adopted AS stk_adopted, p.uid_adopted AS uid_adopted, p.stk_grd AS stk_grd, p.qc_pct AS qc_pct, p.unit_wgt AS unit_wgt, p.unit_vol AS unit_vol, p.unit_len AS unit_len, p.unit_wd AS unit_wd, p.unit_ht AS unit_ht, p.std_pack_qty AS std_pack_qty, p.std_pack_wgt AS std_pack_wgt, p.std_pack_vol AS std_pack_vol, p.std_pack_len AS std_pack_len, p.std_pack_wd AS std_pack_wd, p.std_pack_ht AS std_pack_ht, p.inner_pck AS inner_pck, p.pck_type AS pck_type, p.is_pgb AS is_pgb, p.prod_link AS prod_link, p.shared AS shared, p.ctrl_unit_id AS ctrl_unit_id, p.multi_color AS multi_color, p.color_id AS color_id, p.color_cmt AS color_cmt, p.multi_spec AS multi_spec, p.spec_grp_id AS spec_grp_id, p.spec_scp_id AS spec_scp_id, p.spec_id AS spec_id, p.spec_cmt AS spec_cmt, p.multi_edition AS multi_edition, p.edition AS edition, p.edition_cmt AS edition_cmt, p.input_code AS input_code, p.lbl_bc AS lbl_bc, p.seq_num AS seq_num, p.description AS description, p.prod_prop_1 AS prod_prop_1, p.prod_prop_2 AS prod_prop_2, p.prod_status AS prod_status, p.opr_id AS opr_id, p.upd_time AS upd_time, p.syn_sn AS syn_sn, p.is_del AS is_del, p.create_by AS create_by, p.create_time AS create_time, p.update_by AS update_by, p.update_time AS update_time, p.sellingPoints AS sellingPoints, p.ingredient AS ingredient FROM `UNIT_PROD_CLS` c INNER JOIN `prod_cls` p ON p.`prod_cls_id` = c.`prod_cls_id` AND c.unit_id =#{unitId} LEFT JOIN sys_prsnl ps ON p.`opr_id` = ps.`prsnl_id` INNER JOIN sys_unit u ON c.`unit_id` = u.`unit_id` LEFT JOIN vender v ON c.`vender_id` = v.`vender_id` AND v.`owner_id` = c.`unit_id` LEFT JOIN sys_unit ui ON v.`vender_id` = ui.`unit_id` INNER JOIN brand bra ON bra.`brand_id` = p.`brand_id` LEFT JOIN color co ON p.`color_id` = co.`color_id` LEFT JOIN `spec_grp` sp ON p.`spec_grp_id` = sp.`spec_grp_id` LEFT JOIN `spec_scp` scp ON p.`spec_scp_id` = scp.`spec_scp_id` LEFT JOIN sys_unit suv ON p.`mfr_id` = suv.unit_id LEFT JOIN sys_unit dsun ON p.`dsgn_unit_id` = dsun.`unit_id` LEFT JOIN sys_prsnl dsunpr ON p.`stylist_id` = dsunpr.`prsnl_id` LEFT JOIN spec sss ON p.`spec_id` = sss.`spec_id` LEFT JOIN sys_code_dtl d1 ON p.`sub_model` = d1.`code` AND d1.`code_type` = "SUB_STYLE" LEFT JOIN sys_code_dtl d2 ON p.`gender` = d2.`code` AND d2.`code_type` = "GENDER" LEFT JOIN sys_code_dtl d3 ON p.`suite_prop` = d3.`code` AND d3.`code_type` = "SUITE_PROP" LEFT JOIN sys_code_dtl d4 ON p.`main_style` = d4.`code` AND d4.`code_type` = "MAIN_STYLE" LEFT JOIN sys_code_dtl d5 ON p.`assis_style` = d5.`code` AND d5.`code_type` = "MAIN_STYLE" LEFT JOIN sys_code_dtl d6 ON p.`uom` = d6.`code` AND d6.`code_type` = "UOM" LEFT JOIN sys_code_dtl d7 ON p.`season` = d7.`code` AND d7.`code_type` = "season" LEFT JOIN sys_code_dtl d8 ON p.`mkt_grd` = d8.`code` AND d8.`code_type` = "MKT_GRD" LEFT JOIN sys_code_dtl d9 ON p.`mkt_sort` = d9.`code` AND d9.`code_type` = "MKT_SORT" LEFT JOIN sys_code_dtl d10 ON p.`mkt_type` = d10.`code` AND d10.`code_type` = "MKT_TYPE" LEFT JOIN sys_code_dtl d11 ON p.`sales_chnl` = d11.`code` AND d11.`code_type` = "SALES_CHNL" LEFT JOIN sys_code_dtl d12 ON p.`prc_lvl` = d12.`code` AND d12.`code_type` = "PRC_LVL" LEFT JOIN sys_code_dtl d13 ON p.`edition` = d13.`code` AND d13.`code_type` = "EDITION" LEFT JOIN sys_code_dtl d14 ON p.`out_mtrl` = d14.`code` AND d14.`code_type` = "ASSIS_STYLE" LEFT JOIN sys_code_dtl d15 ON p.`in_mtrl` = d15.`code` AND d15.`code_type` = "CUP_IN_MTRL" LEFT JOIN sys_code_dtl d16 ON p.`prod_std` = d16.`code` AND d16.`code_type` = "PROD_STD" LEFT JOIN sys_code_dtl d17 ON p.`prod_grd` = d17.`code` AND d17.`code_type` = "CCR_LVL" LEFT JOIN sys_code_dtl d18 ON p.`prod_style` = d18.`code` AND d18.`code_type` = "PROD_STYLE" LEFT JOIN sys_code_dtl d19 ON p.`story_line` = d19.`code` AND d19.`code_type` = "STORY_LINE" LEFT JOIN sys_code_dtl d20 ON p.`stk_grd` = d20.`code` AND d20.`code_type` = "STK_GRD" LEFT JOIN sys_code_dtl d21 ON p.`inner_pck` = d21.`code` AND d21.`code_type` = "INNER_PCK" LEFT JOIN sys_code_dtl d22 ON p.`pck_type` = d22.`code` AND d22.`code_type` = "PCK_type" LEFT JOIN sys_code_dtl d23 ON c.`sales_mode` = d23.`code` AND d23.`code_type` = "RT_MODE" LEFT JOIN sys_code_dtl d24 ON p.`mfr_brand_id` = d24.`code` AND d24.`code_type` = "BRAND_ID" LEFT JOIN sys_code_dtl d25 ON p.`prod_line` = d25.`code` AND d25.`code_type` = "PROD_LINE" LEFT JOIN spec_grp d26 ON p.`spec_grp_id` = d26.`spec_grp_id` LEFT JOIN sys_code_dtl d27 ON p.`auditType` = d27.code AND d27.`code_type` = 'auditType' WHERE p.`is_del` = 1 AND p.`prod_status` != 'D' AND p.`ctrl_unit_id` =#{unitId} AND p.`shared` = 'T' /*商品品种Id*/
and p.prod_cls_id = #{prodClsId,jdbcType=BIGINT}
/*商品代码*/
and instr( p.prod_cls_code,#{prodClsCode,jdbcType=VARCHAR})>0
/*商品名称*/
and instr(p.prod_name , #{prodName,jdbcType=BIGINT})>0
/*商品分类Id*/
and instr(p.prod_cat_id,#{prodCatId,jdbcType=VARCHAR}) >0
/*商品品牌Id*/
and p.brand_id=#{brandId,jdbcType=BIGINT}
and instr(p.input_code,#{inputCode,jdbcType=VARCHAR})>0
/*商品状态*/
and p.prod_status=#{goodsStatus}
AND p.`prod_cls_id` IN (
#{item.prodClsId}
)
AND p.`prod_cls_id` =0

select  from prod_cls

    prod_cls_id in (

        #{prodClsId}

    )

SELECT pc.prod_cls_id AS prodClsId, pc.prod_cls_code AS prodClsCode, pc.prod_name AS prodName, pc.input_code AS inputCode, pc.prod_status AS prodStatus FROM unit_prod_cls upc INNER JOIN prod_cls pc ON pc.prod_cls_id = upc.prod_cls_id INNER JOIN ( SELECT y.brand_id AS brand_id FROM ( SELECT DISTINCT u.brand_id AS brand_id FROM user_brand u WHERE u.user_id = #{userId} AND u.owner_id = #{sUnitId} AND u.is_del = 1 UNION SELECT DISTINCT d.brand_id AS brand_id FROM user_bg u INNER JOIN bg_dtl d ON u.bg_id = d.bg_id WHERE u.user_id = #{userId} AND u.owner_id = #{sUnitId} AND u.is_del = 1 ) y INNER JOIN brand a ON y.brand_id = a.`brand_id` AND a.brand_status = 'A' ) userP ON userP.brand_id = pc.brand_id LEFT JOIN color c ON c.color_id = pc.color_id AND c.deleted = 'F' LEFT JOIN spec s ON s.spec_id = pc.spec_id AND s.deleted = 'F' LEFT JOIN sys_code_dtl scd ON scd.`code` = pc.edition AND scd.code_type = 'EDITION' LEFT JOIN sys_code_dtl scd1 ON scd1.`code` = pc.uom AND scd1.code_type = 'UOM' WHERE pc.isAudit = 1 AND pc.auditType = 'am' AND pc.prod_status != 'D' AND upc.unit_id = #{sUnitId}
AND pc.prod_cls_code LIKE CONCAT(#{prodClsCode}, '%')
AND pc.prod_cat_id LIKE CONCAT(#{prodCatId}, '%')
AND pc.prod_name like CONCAT('%',#{prodName}, '%')
AND pc.input_code LIKE CONCAT(#{inputCode}, '%')
AND pc.brand_id = #{brandId}
AND pc.prod_status = #{prodStatus}
