mapper.select:
select

from src
where unit_id = #{unitId,jdbcType=BIGINT}
and src_num = #{srcNum,jdbcType=VARCHAR}

mapper.delete:
delete from src
where unit_id = #{unitId,jdbcType=BIGINT}
  and src_num = #{srcNum,jdbcType=VARCHAR}

delete from src
where unit_id = #{unitId}
  and src_num = #{srcNum}

delete from src
where unit_id = #{unitId}
  and rtc_num = #{rtcNum}

mapper.select:
SELECT src.unit_id, src.src_num, rtc.doc_date, src.src_type, rtc.rtc_type, rtc.vender_id, suo.unit_num AS venderNum, su.unit_name AS venderName, rtc.vendee_id, suo10.unit_num AS vendeeNum, su10.unit_name AS vendeeName, v.psa_ctlr AS psaCtrl, rtc.vde_wareh_id, suo1.unit_num AS vdeWarehNum, su1.unit_name AS vdeWarehName, rtc.vdr_wareh_id, suo2.unit_num AS vdrWarehNum, su2.unit_name AS vdrWarehName, rtc.st_unit_id, suo3.unit_num AS stUnitNum, su3.unit_name AS stUnitName, rtc.st_wareh_id, suo4.unit_num AS stWarehNum, su4.unit_name AS stWarehName, rtc.end_unit_id, suo5.unit_num AS endUnitNum, su5.unit_name AS endUnitName, rtc.end_wareh_id, suo6.unit_num AS endWarehNum, su6.unit_name AS endWarehName, rtc.tran_unit_id, suo7.unit_num AS tranUnitNum, su7.unit_name AS tranUnitName, rtc.vde_fscl_unit_id, su8.unit_code AS vdeFsclUnitCode, su8.unit_name AS vdeFsclUnitName, rtc.vdr_fscl_unit_id, su9.unit_code AS vdrFsclUnitCode, su9.unit_name AS vdrFsclUnitName, rtc.vde_invd, rtc.src_auto_gen, rtc.src_auto_chk, rtc.prc_auto_gen, rtc.src_gen, rtc.prc_gen, rtc.prc_auto_chk, rtc.prc_site, rtc.reqd_date, rtc.is_cms, rtc.psc_reqd, rtc.psc_num, src.sr_brdg_mode, rtc.is_pr_itmd, rtc.is_sr_itmd, rtc.multi_impl, rtc.dr_diff_jgd, rtc.rtna_invd, rtc.acc_enabled, rtc.rtn_acc_site, rtc.spl_enabled, rtc.exbl_rate, rtc.acc_val, rtc.src_doc_type, rtc.src_doc_num, rtc.prod_cat_id, pc.prod_cat_name AS prodCatName, rtc.rtn_type, rtc.expd_date, rtc.deliv_mthd, rtc.deliv_pstd, rtc.deliv_addr, rtc.bxi_enabled, rtc.ttl_qty, rtc.ttl_box, rtc.ttl_val, rtc.ttl_tax, rtc.ttl_mkv, rtc.ttl_deliv_qty, rtc.ttl_deliv_box, rtc.ttl_deliv_val, rtc.ttl_deliv_tax, rtc.ttl_deliv_mkv, rtc.ttl_rcv_qty, rtc.ttl_rcv_box, rtc.ttl_rcv_val, rtc.ttl_rcv_tax, rtc.ttl_rcv_mkv, rtc.opr_id, sp.prsnl_code AS oprCode, sp.full_name AS oprName, DATE_FORMAT(rtc.op_time,'%Y-%m-%d %H:%i:%S') AS opTime, src.chkr_id, spo1.prsnl_num AS chkrNum, sp1.full_name AS chkrName, DATE_FORMAT(src.chk_time,'%Y-%m-%d %H:%i:%S') AS chkTime, rtc.effective, rtc.progress, src.suspended, rtc.cancelled, rtc.exec_rtc_num, rtc.itmd_rtc_num, rtc.st_rtc_num, rtc.end_rtc_num, src.af_reqd, src.af_bnd, src.af_bndr_id, spo2.prsnl_num AS acBndrNum, sp2.full_name AS acBndrName, src.af_bnd_time, src.pr_fwdd, src.rtc_num, rtc.remarks, rtc.src_doc_unit_id FROM ( SELECT a.* FROM src a WHERE 1 = 1 ) src INNER JOIN rtc rtc ON rtc.rtc_num = src.rtc_num LEFT JOIN sys_unit su ON su.unit_id = rtc.vender_id LEFT JOIN sys_unit_owner suo ON suo.unit_id = rtc.vender_id AND suo.owner_id = rtc.vendee_id AND suo.deleted = 'F' LEFT JOIN sys_unit su10 ON su10.unit_id = rtc.vendee_id LEFT JOIN sys_unit_owner suo10 ON suo10.unit_id = rtc.vendee_id AND suo10.owner_id = rtc.vender_id AND suo10.deleted = 'F'
LEFT JOIN vendee v ON v.vendee_id = rtc.vendee_id AND v.owner_id = rtc.vender_id AND v.vde_status='A' LEFT JOIN sys_unit su1 ON su1.unit_id = rtc.vde_wareh_id LEFT JOIN sys_unit_owner suo1 ON suo1.unit_id = rtc.vde_wareh_id AND suo1.owner_id = rtc.vendee_id AND suo1.deleted = 'F' LEFT JOIN sys_unit su2 ON su2.unit_id = rtc.vdr_wareh_id LEFT JOIN sys_unit_owner suo2 ON suo2.unit_id = rtc.vdr_wareh_id AND suo2.owner_id = rtc.vender_id AND suo2.deleted = 'F' LEFT JOIN sys_unit su3 ON su3.unit_id = rtc.st_unit_id LEFT JOIN sys_unit_owner suo3 ON suo3.unit_id = rtc.st_unit_id AND suo3.owner_id = rtc.vendee_id AND suo3.deleted = 'F' LEFT JOIN sys_unit su4 ON su4.unit_id = rtc.st_wareh_id LEFT JOIN sys_unit_owner suo4 ON suo4.unit_id = rtc.st_wareh_id AND suo4.owner_id = rtc.st_unit_id AND suo4.deleted = 'F' LEFT JOIN sys_unit su5 ON su5.unit_id = rtc.end_unit_id LEFT JOIN sys_unit_owner suo5 ON suo5.unit_id = rtc.end_unit_id AND suo5.owner_id = rtc.vendee_id AND suo5.deleted = 'F' LEFT JOIN sys_unit su6 ON su6.unit_id = rtc.end_wareh_id LEFT JOIN sys_unit_owner suo6 ON suo6.unit_id = rtc.end_wareh_id AND suo6.owner_id = rtc.end_unit_id AND suo6.deleted = 'F' LEFT JOIN sys_unit su7 ON su7.unit_id = rtc.tran_unit_id LEFT JOIN sys_unit_owner suo7 ON suo7.unit_id = rtc.tran_unit_id AND suo7.owner_id = rtc.vendee_id AND suo7.deleted = 'F' LEFT JOIN sys_unit su8 ON su8.unit_id = rtc.vde_fscl_unit_id AND su8.unit_status='A' LEFT JOIN sys_unit su9 ON su9.unit_id = rtc.vdr_fscl_unit_id AND su8.unit_status='A' LEFT JOIN prod_cat pc ON pc.prod_cat_id = rtc.prod_cat_id LEFT JOIN sys_prsnl sp ON sp.prsnl_id = rtc.opr_id LEFT JOIN sys_prsnl sp1 ON sp1.prsnl_id = src.chkr_id LEFT JOIN sys_prsnl_owner spo1 ON spo1.prsnl_id = src.chkr_id LEFT JOIN sys_prsnl sp2 ON sp2.prsnl_id = src.af_bndr_id LEFT JOIN sys_prsnl_owner spo2 ON spo2.prsnl_id = src.af_bndr_id WHERE 1=1
and src.src_num like CONCAT( #{srcNum}, '%')
and src.rtc_num like CONCAT( #{rtcNum} )
and suo.unit_num like CONCAT( #{venderNum}, '%')
and suo1.unit_num like CONCAT( #{vdrWarehNum}, '%')
and suo2.unit_num like CONCAT( #{vdeWarehNum}, '%')
and rtc.progress = #{progress}
and rtc.effective = #{effective}
and rtc.doc_date >= #{startTime}
and rtc.doc_date <= #{endTime}

