ve, rtc.progress, src.suspended, rtc.cancelled, rtc.exec_rtc_num, rtc.itmd_rtc_num, rtc.st_rtc_num, rtc.end_rtc_num, src.af_reqd, src.af_bnd, src.af_bndr_id, spo2.prsnl_num AS acBndrNum, sp2.full_name AS acBndrName, src.af_bnd_time, src.pr_fwdd, src.rtc_num, rtc.remarks, rtc.src_doc_unit_id FROM ( SELECT a.* FROM src a WHERE 1 = 1 and a.unit_id = #{sUnitId} ) src INNER JOIN rtc rtc ON rtc.rtc_num = src.rtc_num LEFT JOIN sys_unit su ON su.unit_id = rtc.vender_id LEFT JOIN sys_unit_owner suo ON suo.unit_id = rtc.vender_id AND suo.owner_id = rtc.vendee_id AND suo.deleted = 'F' LEFT JOIN sys_unit su10 ON su10.unit_id = rtc.vendee_id LEFT JOIN sys_unit_owner suo10 ON suo10.unit_id = rtc.vendee_id AND suo10.owner_id = rtc.vender_id AND suo10.deleted = 'F'
